import React from 'react';
import Particles from 'react-tsparticles';

const ParticleSettings = () => {
    return (
        <div>
           <Particles
            height="1000px"
            width="100vw"
            id="tsparticles"
            options={{
                background:{
                    color:{
                        value:"#0d47a1"
                    },
                },
                fpslimit:60,
                interactivity:{
                    detect_on:'canvas',
                    events:{
                        onClick:{
                            enable:'true',
                            move:'push'
                        },
                        onHover:{
                            enable:'true',
                            mode:'repulse'
                        }
                    }
                }
            }} 
           /> 
        </div>
    )
}

export default ParticleSettings
